package edu.deanza.cis22c.w2018.team1.structure.queue;/** * An interface for the ADT queue. * * @author Frank M. Carrano * @author Timothy M. Henry * @version 4.0 * UPDATED by C. Lee-Klawender */public interface QueueInterface<T> {	/**	 * Adds a new entry to the back of this queue.	 *	 * @param newEntry An object to be added.	 * @return True if succesfully added the newEntry, false otherwise	 */	boolean enqueue(T newEntry);	/**	 * Removes and returns the entry at the front of this queue.	 *	 * @return The object at the front of the queue	 * or null if the queue is empty before the operation.	 */	T dequeue();	/**	 * Retrieves the entry at the front of this queue.	 *	 * @return The object at the front of the queue	 * or null if the queue is empty.	 */	T peekFront();	/**	 * Detects whether this queue is empty.	 *	 * @return True if the queue is empty, or false otherwise.	 */	boolean isEmpty();	/**	 * Returns number of items in this queue	 *	 * @return Number of items	 */	int size();}